import React, { Component } from 'react'

export default class Details extends Component {
    constructor(){
        super();
        this.state = {
            firstname: "",
            myFirstname:"",
            lastname: "",
            mylastname:""
        }
    }
    onChangename=(event)=>{ 
            this.setState({firstname: event.target.value});
            this.setState({lastname:event.target.value});

    }

    changeDiv=()=>{
        this.setState({myFirstname: this.state.firstname});
        this.setState({mylastname: this.state.firstname});

    }
    render() {
    return (
      <div>
          <div className='App'>
        <input type="text" value={this.state.firstname} onChange={this.onChangename}/>
          </div>
          <div className='App'>
        <input type="text" value={this.state.firstname} onChange={this.onChangename}/>
          </div>

          <button onClick={this.changeDiv}>hi</button>
          <div>
              {this.state.myFirstname}
          </div>

      </div>
    )

  }
}
