import React, { Component } from 'react'

export default class Details extends Component {
    constructor(){
        super();
        this.state = {
            firstname: "",
            myFirstname:"",
            lastname: "",
            mylastname:"",
            age:""
        }
    }
    onChangename=(event)=>{ 
        this.setState({firstname: event.target.value});       
    }
    onChangeLname=(event)=>{ 
        this.setState({lastname:event.target.value});       
    }
    onChangeAgename=(event)=>{ 
        this.setState({lastname:event.target.value});       
    }

    changeDiv=()=>{
        this.setState({
            myFirstname: this.state.firstname,
            mylastname: this.state.lastname

        });}
    render() {
    return (
      <div>
          <div className='App'>
        <input type="text" value={this.state.firstname} onChange={this.onChangename}/>
          </div>
          <div className='App'>
        <input type="text" value={this.state.lastname} onChange={this.onChangeLname}/>
          </div>

          <button onClick={this.changeDiv}>hi</button>
          <div>
              {this.state.myFirstname}
              {this.state.mylastname}
          </div>
          <div>
          </div>

      </div>
    )

  }
}
