import React, { Component } from 'react'

export default class UpperLowerCase extends Component {

        constructor(){
      super()
      this.state={
          text: "",
          convert:"",

      }
      }
     textChange  =(event)=>{
        this.setState({
            text:event.target.value,
            // var:this.textarea.length,       
        })
    }
    change =(event)=>{
        this.setState({
            convert:this.state.text.toUpperCase(),
            // var:this.state.text.length,
            // count:this.state.text.split(" ").length
        })
    }
    lower = (event)=>{
        this.setState({
           convert:this.state.text.toLowerCase(),
            // var:this.state.text.length,
            // count:this.state.text.split(" ").length


        })
    }
  render() {
    return (
        <div>
            <div className="app">
        <label>Text Area:</label>
        <input value={this.state.text} id="users" onChange={this.textChange}>{this.state.convert}</input>
      </div> 
      <button onClick={this.change}>UpperCase</button>
      <button onClick={this.lower}>LowerCase</button>
      <div>
      <h1>Utility Name: {this.state.convert}</h1>
      <h1>Utility Character:{this.state.var}</h1>
      <h1> word count:{this.state.count}</h1>
      </div>
      </div>
    )
  }
}
