import React, { Component } from 'react'

export default class Details extends Component {
    constructor(){
        super();
        this.state = {
            text: "",
            myText:""
        }
    }
    onChangename=(event)=>{ 
            this.setState({text: event.target.value});

    }
    changeDiv=()=>{
        this.setState({text: this.target.value});
    }
    render() {
    return (
      <div>
          <input type="text" placeholder="first name" value={this.state.text} onChange={this.onChangename}/>

          <button onClick={this.changeDiv}>hi</button>

      </div>
    )
  }
}
