import React, { Component } from 'react'

export default class Wordcount extends Component {
    constructor(){
        super();
        this.state = {
            name: "",
            namelength:"",
        }
    }
    onChangename=(event)=>{ 
        this.setState({namelength: event.target.value});       
    }
    changeDiv=()=>{
        this.setState({
            namelength: this.state.name,

        });}

  render() {
    return (
      <div>
          <div className='App'>
        <input type="text" value={this.state.namelength} onChange={this.onChangename} placeholder="first name"/>
          </div>

         Number of words in Your Name: {this.state.myFirstname} {this.state.mylastname}

      </div>
    )
  }
}
